package com.typecasting;

public class UniCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 'a';
		System.out.println(i);
		int j = 'A';
		System.out.println(j);
		int k = '0';
		System.out.println(k);
		
		char c = 'a';
		System.out.println("c: " + c);

	}

}
